<?php namespace themeDevSocialNext\Providers;use \themeDevSocialNext\Providers\Abst\Abstractnext as Abstractnext;use \themeDevSocialNext\Providers\Abst\Util as Util;use \themeDevSocialNext\Providers\Abst\Curl as Curl;class Google extends Abstractnext{protected $api_key='';protected $api_secret='';private $access_token='';private $access_code='';protected $apiBaseUrl='https://www.googleapis.com/';protected $authorizeUrl='https://accounts.google.com/o/oauth2/auth';protected $accessTokenUrl='https://accounts.google.com/o/oauth2/token';protected $apiDocumentation='https://developers.google.com/identity/protocols/OAuth2';protected $profileUrlTemplate='https://profiles.google.com/%s';protected $scope='https://www.googleapis.com/auth/userinfo.profile https://www.googleapis.com/auth/userinfo.email';protected $callback='';protected $fields='';private $nx_connected=false;private $profile_data='';private $curl;public function __construct($config){if(is_array($config)){$this->api_key=isset($config['keys']['id'])?$config['keys']['id']:'';$this->api_secret=isset($config['keys']['secret'])?$config['keys']['secret']:'';$this->callback=isset($config['callback'])?$config['callback']:'';$this->curl=new Curl();}}public function nx_authorise(){if(isset($_GET['code'])){$this->get_access_token();}else if(isset($_GET['error'])){return $_GET['error'];}else{$params=['response_type'=>'code','client_id'=>$this->api_key,'scope'=>$this->scope,'redirect_uri'=>$this->callback,];$url=$this->authorizeUrl.'?'.http_build_query($params);Util::redirect($url);}}protected function get_access_token(){$this->access_code=isset($_GET['code'])?$_GET['code']:'';if(strlen($this->access_code)>1){$credentials=$this->api_key.':'.$this->api_secret;$toSend=base64_encode($credentials);$header=['Authorization'=>'Basic '.$toSend,'Content-Type'=>'application/x-www-form-urlencoded;charset=UTF-8'];$body=['grant_type'=>'authorization_code','code'=>$this->access_code,'client_id'=>$this->api_key,'client_secret'=>$this->api_secret,'redirect_uri'=>$this->callback];$reoponse=$this->curl->request($this->accessTokenUrl,'POST',$body,$header);$reoponse=@json_decode($reoponse,true);if(isset($reoponse['access_token'])){$this->access_token=$reoponse['access_token'];$this->nx_connected=true;$this->get_api_data();}else{echo isset($reoponse['error_description'])?$reoponse['error_description']:'';}}}protected function get_api_data(){if(strlen($this->access_token)>5){$header=['Authorization'=>sprintf('Bearer %s',$this->access_token),'Content-Type'=>'application/json',];$body=[];try{$url=$this->apiBaseUrl.'oauth2/v3/userinfo/?access_token='.$this->access_token;$reoponse=$this->curl->request($url,'GET',$body,$header);$reoponse=@json_decode($reoponse,true);if(isset($reoponse['sub'])&&strlen($reoponse['sub'])>0){$userProfile=new \stdClass();$userProfile->identifier=strlen($reoponse['sub'])?$reoponse['sub']:substr(md5(microtime()),rand(0,26),10);$userProfile->firstName=isset($reoponse['given_name'])?$reoponse['given_name']:'';$userProfile->lastName=isset($reoponse['family_name'])?$reoponse['family_name']:'';$userProfile->displayName=isset($reoponse['name'])?$reoponse['name']:'';$userProfile->email=isset($reoponse['email'])?$reoponse['email']:$userProfile->identifier;$userProfile->description='';$userProfile->emailVerified=true;$userProfile->photoURL=isset($reoponse['picture'])?$reoponse['picture']:'';$userProfile->webSiteURL=isset($reoponse['website'])?$reoponse['website']:'';$userProfile->gender='';$userProfile->language='';$profileUrl=sprintf($this->profileUrlTemplate,'me');$userProfile->profileURL=$profileUrl;$userProfile->region='';$userProfile->data='';$this->profile_data=$userProfile;}else{$this->profile_data=' Sorry could\'t found profile information.';}}catch(Exception $e){$this->profile_data='Sorry api error';}}else{$this->profile_data='Invalid Accexx Token';}return $this->profile_data;}public function is_connected(){return $this->nx_connected;}public function getUserProfile(){return $this->profil